        private readonly HttpClientHandler httpClientHandler;

        public MockHttpClientHandlerWeatherGen()
        {
            this.httpClientHandler = this.GenerateMockHttpClientHandler();
        }

        public HttpClientHandler GetMockHttpClientHandler()
        {
            return this.httpClientHandler;
        }

        private HttpClientHandler GenerateMockHttpClientHandler()
        {
            var mockClient = new Mock<HttpClientHandler>(MockBehavior.Strict);
            this.SetHttpMockBehavior(ref mockClient);
            return mockClient.Object;
        }

        private void SetHttpMockBehavior(ref Mock<HttpClientHandler> mockClient)
        {
            mockClient
               .Protected()
               .Setup<Task<HttpResponseMessage>>(
               MockData.SendAsync,
               ItExpr.Is<HttpRequestMessage>(r => r.RequestUri.ToString().StartsWith("https://atlas.microsoft.com/search/address/json")),
               ItExpr.IsAny<CancellationToken>())
               .ReturnsAsync(() => new HttpResponseMessage()
               {
                   Content = new StringContent(this.GetAzureMapsCoordinates()),
               });

            mockClient
               .Protected()
               .Setup<Task<HttpResponseMessage>>(
               MockData.SendAsync,
               ItExpr.Is<HttpRequestMessage>(r => r.RequestUri.ToString().StartsWith("https://atlas.microsoft.com/search/address/reverse/json")),
               ItExpr.IsAny<CancellationToken>())
               .ReturnsAsync(() => new HttpResponseMessage()
               {
                   Content = new StringContent(this.GetAzureMapsAddress()),
               });

            mockClient
               .Protected()
               .Setup<Task<HttpResponseMessage>>(
               MockData.SendAsync,
               ItExpr.Is<HttpRequestMessage>(r => r.RequestUri.ToString().StartsWith("https://atlas.microsoft.com/weather/forecast/daily/json")),
               ItExpr.IsAny<CancellationToken>())
               .ReturnsAsync(() => new HttpResponseMessage()
               {
                   Content = new StringContent(this.GetAzureMapsWeatherDaily()),
               });

            mockClient
               .Protected()
               .Setup<Task<HttpResponseMessage>>(
               MockData.SendAsync,
               ItExpr.Is<HttpRequestMessage>(r => r.RequestUri.ToString().StartsWith("https://atlas.microsoft.com/weather/forecast/hourly/json")),
               ItExpr.IsAny<CancellationToken>())
               .ReturnsAsync(() => new HttpResponseMessage()
               {
                   Content = new StringContent(this.GetAzureMapsWeatherHourly()),
               });
        }

